class BidCollusionDetector:
    def __init__(self, threshold=0.9, abnormal_threshold=0.1):
        """
        初始化围标串标检测器
        :param threshold: 相似度阈值（高于此值认为异常相似）
        :param abnormal_threshold: 异常分数比例阈值
        """
        self.threshold = threshold
        self.abnormal_threshold = abnormal_threshold
        self.scores_df = None

    def add_bid_scores(self, scores_list):
        """
        添加多个投标文件得分
        :param scores_list: 评分列表，每个元素包含 company, total_score, dimension_scores
        """
        data = []
        for score_info in scores_list:
            company = score_info.get('company', 'unknown')
            total = score_info['total_score']
            dims = score_info['dimension_scores']

            record = {'company': company, 'total_score': total}
            record.update(dims)
            data.append(record)

        self.scores_df = pd.DataFrame(data)

    def detect_score_abnormalities(self):
        """检测异常分数分布"""
        if self.scores_df is None or len(self.scores_df) < 2:
            return {}

        anomalies = {}

        # 创建排序后的DataFrame（按总分降序）
        sorted_df = self.scores_df.sort_values('total_score', ascending=False).reset_index(drop=True)
        mean_total = self.scores_df['total_score'].mean()
        std_total = self.scores_df['total_score'].std()
        half_mean = mean_total / 2

        # 分差阈值法
        if len(sorted_df) >= 2:
            top_score = sorted_df.iloc[0]['total_score']
            second_score = sorted_df.iloc[1]['total_score']
            score_gap = top_score - second_score

            # 动态阈值：分差 > 15分 或 > 第二名分数的20%
            if score_gap > max(15, second_score * 0.2):
                anomalies[sorted_df.iloc[0]['company']] = {
                    'type': '异常高分(串标嫌疑)',
                    'gap': score_gap,
                    'score': top_score,
                    'direction': 'high'
                }

        # Z分数法
        if len(sorted_df) >= 4:
            # 计算Z分数
            self.scores_df['z_score'] = (self.scores_df['total_score'] - mean_total) / (std_total + 1e-8)

            # 检测异常高/低分
            for idx, row in self.scores_df.iterrows():
                z_score = row['z_score']
                abs_z = abs(z_score)
                company = row['company']

                # 如果公司已在分差阈值法中被检测到，跳过避免重复
                if company in anomalies:
                    continue

                # 高分异常 - 串标嫌疑
                if z_score > 2:
                    anomalies[company] = {
                        'type': '异常高分(串标嫌疑)',
                        'z_score': z_score,
                        'score': row['total_score'],
                        'mean': mean_total,
                        'direction': 'high'
                    }
                # 低分异常 - 陪标嫌疑
                elif z_score < -2.0 or row['total_score'] < half_mean:
                    type_label = '异常低分(陪标嫌疑)'
                    if row['total_score'] < half_mean:
                        type_label = '异常低分(陪标嫌疑)'
                    anomalies[company] = {
                        'type': type_label,
                        'z_score': z_score,
                        'score': row['total_score'],
                        'mean': mean_total,
                        'direction': 'low'
                    }

        return anomalies

    def generate_report(self):
        """生成围标检测报告"""
        if self.scores_df is None or len(self.scores_df) == 0:
            return "没有可分析的数据"

        report = []
        total = self.scores_df['total_score']

        report.append("【分数检测报告】")
        report.append(f" 分析时间: {datetime.datetime.now().strftime('%Y-%m-%d %H:%M:%S')}")
        report.append(f" 投标文件数量: {len(self.scores_df)}")
        report.append(f" 总分范围: {min(total):.2f} - {max(total):.2f}")
        report.append(f" 平均总分: {total.mean():.2f} ± {total.std():.2f}")

        # 生成排名，降序排序
        self.scores_df['rank'] = self.scores_df['total_score'].rank(method='min', ascending=False).astype(int)
        sorted_df = self.scores_df.sort_values('total_score', ascending=False).reset_index(drop=True)

        # 排名分析
        report.append("\n投标排名情况：")
        report.append(sorted_df[['rank', 'company', 'total_score']].to_string(index=False))
        report.append("（如果中标企业排序较后，或分数较低，则怀疑存在串标）")

        # 异常检测结果
        abnormalities = self.detect_score_abnormalities()

        if abnormalities:
            report.append("\n异常分数情况：")

            # 分别输出高分和低分异常
            high_abnormalities = {k: v for k, v in abnormalities.items() if v['direction'] == 'high'}
            low_abnormalities = {k: v for k, v in abnormalities.items() if v['direction'] == 'low'}

            if high_abnormalities:
                report.append("异常高分情况(串标嫌疑):")
                for comp, info in high_abnormalities.items():
                    if 'z_score' in info:
                        report.append(
                            f"{comp}，总分={info['score']:.2f} (Z_score={info['z_score']:.2f})")
                    else:
                        report.append(
                            f"{comp}，总分={info['score']:.2f} (高出第二名{info['gap']:.2f}分)")

            if low_abnormalities:
                report.append("异常低分情况(陪标嫌疑):")
                for comp, info in low_abnormalities.items():
                    report_details = f"{comp}，总分={info['score']:.2f} "

                    if 'z_score' in info:
                        report_details += f"(分数过低"
                    if 'half_mean' in info and info['score'] < info['half_mean']:
                        report_details += f"(分数过低"

                    elif 'z_score' in info:
                        report.append(report_details + ")")
                    else:
                        report.append(report_details + ")")
            else:
                report.append("\n 未检测到明显异常分数情况")

        return "\n".join(report)


